#! /bin/sh

swaggen generate soracom-sandbox-api.en.json \
  --template Template \
  --destination auto-generated-code-output

swaggen generate soracom-api.en.json \
  --template Template \
  --destination auto-generated-code-output


cp -R auto-generated-code-output/Sources/Models/* ../../Sources/SoracomAPI/Models/_AutoGeneratedCode/
cp -R auto-generated-code-output/Sources/Requests/_AutoGeneratedCode/* ../../Sources/SoracomAPI/Requests/_AutoGeneratedCode/
cp auto-generated-code-output/Sources/AnyCodable.swift ../../Sources/SoracomAPI/Models/_AutoGeneratedCode/
cp auto-generated-code-output/Sources/API.swift ../../Sources/SoracomAPI/Models/_AutoGeneratedCode/
cp auto-generated-code-output/Sources/Coding.swift ../../Sources/SoracomAPI/Models/_AutoGeneratedCode/

